import { baseURL } from '../global';
import React from 'react';
import DexPokemonCard from "./DexSpecificPokemonCard";

class Dex extends React.Component {
    state = {
        dex: [],
    };

    async componentDidMount() {
        const dexPath = '/v1/pokedex'
        const response = await fetch(baseURL + dexPath)
        const dex = await response.json();

        let monInDex = [];
        for (const pokemonName of Object.keys(dex)) {
            const caught = dex[pokemonName]
            if (caught) {
                const pokemonInfo = await this.getPokemonInDex(pokemonName);
                pokemonInfo.imageName = pokemonName;
                monInDex.push(pokemonInfo);
            }
        }
        this.setState({
            dex: monInDex
        })
    }



    async getPokemonInDex(pokemonName) {
        const pokemonPath = '/v1/pokedex/' + pokemonName;
        const response = await fetch(baseURL + pokemonPath);
        const pokemonInfo = await response.json();
        return pokemonInfo;
    }

    render() {
        return(
            this.state.dex.map((pokemon => <DexGeneralPokemonCard key={pokemon.name} {...pokemon} />))
        );
    }
}

class DexGeneralPokemonCard extends React.Component {
    constructor (props) {
        super(props)

    }

    renderSpecificCard = () => {
        return(<DexPokemonCard key={this.props.name} {...this.props}/>);
    }

    render() {
        return(

            <div className="card" style={{"width": 400+ "px"}} onClick={this.renderSpecificCard}>
                <img src={baseURL + "/v1/pokedex/image/" + this.props.imageName} alt={this.props.name + " image"} />
                <h4 className="card-title">{this.props.name}</h4>
            </div>
        );
    }
}

export default Dex;
