import React from "react";
import { baseURL } from "../global";

const pkm = {
    abilities: {0: "Blaze", H: "Solar Power"},
    baseStats: {hp: 39, atk: 52, def: 43, spa: 60, spd: 50, spe: 65},
    catchRate: 45,
    desc: "It has a preference for hot things. When it rains, steam is said to spout from the tip of its tail.",
    dexNumber: 4,
    eggGroups: (2) ["Monster", "Dragon"],
    evolvesTo: ["Charmeleon"],
    genderRatio: {M: 0.875, F: 0.125},
    height: 0.6,
    name: "Charmander",
    types: ["Fire"],
    weight: 8.5,
}


class DexPokemonCard extends React.Component {
    constructor (props) {
        super(props)
    }

    render() {
        const hpPercent = "calc(100% * {this.props.baseStats.hp} /255)"
        return(
            <div className="card">
                {/* <Link to={{
                    pathname: '/pokemonInfo',
                    state: {
                    test: this.props.pokemon,

                    }
                }}>
                    <img src={this.props.recipe.image} alt="image_source" />
                    <h4 className="card-title">{this.props.pokemon}</h4>
                </Link> */}
                <img src={baseURL + "/v1/pokedex/image/" + this.props.name} alt={this.props.name + " image"} />
                <h4 className="card-title">{this.props.name}</h4>
                <p>{"Dex no: " + this.props.dexNumber}</p>
                <p>{this.props.desc}</p>

                <p>{"Type: " + this.props.types}</p>
                <p>{"Height: " + this.props.height + "m"}</p>
                <p>{"Weight: " + this.props.weight + "kg"}</p>
                <p>{"Egg group: " + this.props.eggGroups}</p>
            </div>
        );
    }
}

class StatBar extends React.Component {

}

export default DexPokemonCard;
