import React, { Component } from 'react';
import { Redirect } from 'react-router';
import * as api from './api.js';
import { withStyles, Typography, TextField, Icon, IconButton } from '@material-ui/core';


const styles = theme => ({
    divStyle: {
        margin: 'auto',
        marginTop: '50px',
        paddingRight: '15pt',
        paddingLeft: '15pt',
        paddingBottom: '50px'
    }
})

class Login extends Component {
    constructor(props) {
        super(props)

        this.state = {
            email: '',
            password: '',
            url: "",
            fireRedirect: false
        }

        this.handleChange = this.handleChange.bind(this);

    }

    handleChange(event) {
        this.setState({ [event.target.name]: event.target.value });
    }

    submitCredentials() {
        this.setState({ isBusy: true })
        let body = JSON.stringify({
            email: this.state.email,
            password: this.state.password
        });
        fetch(api.sessionURL, {
            method: 'POST',
            body: body,
            headers: { 'Content-Type': 'application/json' }
        }).then(resp => {
            if (!resp.ok) {
                resp.text().then(text => {
                    window.alert('Error ' + resp.status + ' - ' + text)
                })
                throw new Error(resp)
            }
            return resp
        }).then(resp => {
            localStorage.setItem('auth', resp.headers.get('Authorization'))
            this.setState({ url: '/documents', fireRedirect: true })
        }).catch(err => {
            console.log(err);
            this.setState({ error: err });
        }).then(() => {
            this.setState({ isBusy: false });
        });
    }

    render() {
        const { fireRedirect } = this.state

        return (
            <div>
                <div className={this.props.classes.divStyle}>
                    <Typography varient="display2" gutterBottom>
                        Login
                </Typography>
                    <TextField
                        required
                        type="email"
                        name="email"
                        label="Email"
                        className={this.props.classes.TextField}
                        value={this.state.email}
                        onChange={this.handleChange}
                    />
                    <TextField
                        required
                        type="password"
                        name="password"
                        label="Password"
                        className={this.props.classes.TextField}
                        value={this.state.password}
                        onChange={this.handleChange}
                    />
                    <Button
                        variant="contained"
                        color="primary"
                        disabled={this.state.isBusy}
                        className={this.props.classes.submitButton}
                        onClick={() => this.submitCredentials()}
                    >Submit</Button>
                    {fireRedirect && (<Redirect to={this.state.url}></Redirect>)}
                </div>
            </div>
        );
    }
}

export default withStyles(styles)(Login);
