import { baseURL } from '../global';
import React from 'react';
import PokemonCard from "./PokemonCard";

class Team extends React.Component {
    state = {
        pokemons: []
    };

    async componentDidMount() {
        const dexPath = '/v1/pokedex'
        const response = await fetch(baseURL + dexPath)
        const dex = await response.json();

        let caughtMon = []
        for (const pokemonName of Object.keys(dex)) {
            const caught = dex[pokemonName]
            if (caught) {
                caughtMon.push(pokemonName)
            }
            this.setState({ caughtMon: caughtMon })
        }
    }

    getTeam() {
        return this.state.pokemons.map((pokemon => <PokemonCard key={pokemon._id} {...pokemon} />));
    }

    render() {
        return(
            <div>
                {this.getTeam()}
            </div>
        );
    }
}

export default Team;
