import React, { Component } from 'react';
import { Redirect } from 'react-router';
import api from './api';
import { Button, FormGroup, FormControl, ControlLabel } from "react-bootstrap";


class SignIn extends Component {
  constructor(props) {
    super(props)

    this.state = {
        email: '',
        password: '',
        fireRedirect: false
    }

    this.handleChange = this.handleChange.bind(this);

}

handleSubmit(event) {
    this.setState({ [event.target.name]: event.target.value });
}

setField = (e) => {
    this.setState({ [e.target.name]: e.target.value });
}

setError = (error) => {
    this.setState({ error })
}

validateForm = () => {
  return this.state.email.length > 0 && this.password.length > 0;
}


submitForm() {
    this.setState({isBusy: true})
    let body = JSON.stringify({
      email: this.state.email, 
      password: this.state.password
    });

    fetch(api.sessionurl, {
      method: "POST",
      body: body, 
      headers: new Headers({
          "Content-Type": "application/json"
      })
    }).then(resp => {
      if (!resp.ok) {
        resp.text().then(text => {
            window.alert('Error ' + resp.status + ' - ' + text)
        })
        throw new Error(resp)
    }
    return resp
    }).then(resp => {
      localStorage.setItem('auth', resp.headers.get('Authorization'))
            this.setState({ url: '/documents', fireRedirect: true })
        }).catch(err => {
            console.log(err);
            this.setState({ error: err });
        }).then(() => {
            this.setState({ isBusy: false });
        });
    }

    render() {
      const { fireRedirect } = this.state
        return (
          <div className="Login">
            <form onSubmit={this.handleSubmit}>
              <FormGroup controlId="email" bsSize="large">
                <ControlLabel>Email</ControlLabel>
                <FormControl
                  autoFocus
                  type="email"
                />
              </FormGroup>
              <FormGroup controlId="password" bsSize="large">
                <ControlLabel>Password</ControlLabel>
                <FormControl
                  value={this.state.password}
                  onChange={(this.handleSubmit)}
                  type="password"
                />
              </FormGroup>
              <Button block bsSize="large" disabled={!validateForm()} type="submit">
                Login
              </Button>
            </form>
          </div>
        );
    }
}

export default SignIn;
