import { baseURL } from '../global';
import React from 'react';

class Inventory extends React.Component {

    state = {
        balls: []
    }

    async componentDidMount() {
        const inventoryPath = "/v1/inventory";
        const response = await fetch(baseURL + inventoryPath);
        const inventory = await response.json();

        let allBallsInfo = [];
        for (const ballName of Object.keys(inventory.balls)) {
            let ballInfo = await this.getBallInfo(ballName);
            ballInfo.remaining = inventory.balls[ballName];

            if (ballInfo) {
                allBallsInfo.push(ballInfo);
            }
        }

        this.setState({
            balls: allBallsInfo
        })
        console.log(this.state.balls)
    }

    async getBallInfo(ballName) {
        const itemPath = "/v1/items/balls/" + ballName;
        const response = await fetch(baseURL + itemPath);
        return await response.json();
    }


    render() {
        return(
            this.state.balls.map((ball => <Item {...ball} />))
        );
    }
}

class Item extends React.Component {
    constructor (props) {
        super(props);
    }

    render() {
        return(
            <div className="card">
                <img src={baseURL + "/v1/items/image/" + this.props.itemCode} alt={this.props.itemName + " image"} />
                <h4>{this.props.itemName}</h4>
                <p>{"Desc: " + this.props.desc}</p>
                <p>{"Remaining: " + this.props.remaining}</p>
            </div>
        );
    }
}

export default Inventory;
