import React from 'react'
import { Link } from 'react-router-dom'

import Form from './Form.js'
import Results from './Results.js'

import { baseURL } from '../global'
import pokemon from './pokemon';
import PokemonCard from "./TeamPokemonCard";

class MainPage extends React.Component {
    constructor(props) {
        super(props)

        this.state = {
            searchResults: [],
            names: [],
            one: '', // one is just a test variable
            images: [],
            pokemon: [],
            savedPokemon: new Map(),
            encountering: {
              isEncountering: false,
              pokemonName: "",
              rarity: "",
            },
            caught: false,
            caughtPokemon: null
        }
    }

    async componentDidMount() {
      const dexPath = '/v1/pokedex'
      const response = await fetch(baseURL + dexPath)
      const dex = await response.json();

      let caughtMon = []
      for (const pokemonName of Object.keys(dex)) {
          const caught = dex[pokemonName]
          if (caught) {
              caughtMon.push(pokemonName)
          }
          this.setState({ caughtMon: caughtMon })
      }
    }

    encounter = async () => {
      const encounterPath = "/v1/encounter";
      const response = await fetch(baseURL + encounterPath);
      const data = await response.json();

      if (this.state.caughtPokemon) {
        this.setState({
          caughtPokemon: null
        });
      }

      this.setState({encountering: {
        isEncountering: true,
        pokemonName: data.pokemonName,
        rarity: data.rarity,
      }})

      console.log(this.state.encountering);
    }

    runaway = async () => {
      const runawayPath = "/v1/runaway";
      const response = await fetch(baseURL + runawayPath);
      const data = await response.text();

      this.setState({encountering: {
        isEncountering: false
      }})
      console.log(data);
    }

    catch = async () => {
      console.log(this.state.encountering);
      const catchPath = "/v1/catch/" + this.state.encountering.pokemonName;
      const response = await fetch(baseURL + catchPath);
      const data = await response.json();

      if (data.status === "success") {
        this.setState({
          encountering: {
            isEncountering: false
          },
          caughtPokemon: data.pokemon
        });
      } else {
        alert("catch failed!");
      }
      // console.log(data.pokemon);
    }

    render = () => {
        let keys = Array.from(this.state.savedPokemon.keys())
        let i = ''
        let qrs = []
        for (i = 0; i < keys.length; i++) {
            qrs[i] = this.state.savedPokemon.get(keys[i])
        }

        const listItems = keys.map((pokemonName, index) => (
            <Link
                to={{
                    pathname: '/pokemonInfo',
                    state: {
                        test: qrs[index],
                    },
                }}
            >
                <li>{pokemonName}</li>
            </Link>
        ))

        return (
            <div>
                {/* <Form
                    placeholderText="Search for a pokemon..."
                    onSubmit={(query) => {
                        this.makeApiRequest(query)
                    }}
                />
                <br></br>
                <hr />

                {this.state.pokemon ? (
                    <Results
                        searchResults={this.state.searchResults}
                        names={this.state.names}
                        one={this.state.one}
                        images={this.state.images}
                        pokemon={this.state.pokemon}
                    />
                ) : (
                    <p>Please type in valid pokemon name</p>
                )}

                <ul>{listItems}</ul> */}
                {!this.state.encountering.isEncountering ? (
                  <button onClick={this.encounter}>
                    Encounter
                  </button>

                ) : (
                  <div>
                    <button onClick={this.catch}>
                      Catch
                    </button>
                    <button onClick={this.runaway}>  
                      Run Away
                    </button>
                    <br/>
                    <div className="card">
                        <img src={baseURL + "/v1/pokedex/image/" + this.state.encountering.pokemonName} alt={this.props.name + " image"} />
                        <h4 className="card-title">{this.state.encountering.pokemonName}</h4>

                    </div>
                  </div>
                )}
              {this.state.caughtPokemon && <PokemonCard {...this.state.caughtPokemon} />}
            </div>
        )
    }

    // makeApiRequest = () => {
    //   let url =
    //   let fetchPromise = fetch(url)
    //   fetchPromise.then((response) => {
    //     response.json().then((data) => {
    //       if (response.status === 200 && data.length > 0) { // Hits has something in it
    //         console.log('response.status', response.status)

    //         const loop = []
    //         const loop2 = []

    //         for (var i = 0; i < data.hits.length; i++) {
    //           console.log(loop[i])
    //           loop[i] = data.hits[i].recipe.label
    //           loop2[i] = data.hits[i].recipe
    //         }

    //         this.setState({
    //           one: data.hits[0].recipe.label,
    //           names: loop,
    //           searchResults: data,
    //           recipes: loop2
    //         })
    //       } else {
    //         this.setState({
    //           names: null,
    //           searchResults: null,
    //           pokemon: null
    //         })
    //       }
    //     })
    //   })
    // }
}

export default MainPage
