import { baseURL } from '../global';
import React from 'react';
import DexSpecificPokemonCard from "./DexSpecificPokemonCard";

class Dex extends React.Component {
    state = {
        dex: [],
        current: [],
    };

    async componentDidMount() {
        const dexPath = '/v1/pokedex'
        const response = await fetch(baseURL + dexPath)
        const dex = await response.json();

        let monInDex = [];
        for (const pokemonName of Object.keys(dex)) {
            const caught = dex[pokemonName]
            if (caught) {
                const pokemonInfo = await this.getPokemonInDex(pokemonName);
                pokemonInfo.imageName = pokemonName;
                monInDex.push(pokemonInfo);
            }
        }
        this.setState({
            dex: monInDex,
            current: monInDex
        })
    }



    async getPokemonInDex(pokemonName) {
        const pokemonPath = '/v1/pokedex/' + pokemonName;
        const response = await fetch(baseURL + pokemonPath);
        const pokemonInfo = await response.json();
        return pokemonInfo;
    }

    filter = (pokemonName) => {
        let filtered = this.state.current.filter((pokemon) => {
            console.log(pokemon.name !== pokemonName)
            return pokemon.name === pokemonName;
        });

        console.log(filtered);
        this.setState({
            current: filtered
        });
    }

    render() {
        return(
            <div style={{"display": "flex", "flexWrap": "wrap"}}>
                {this.state.current.map((pokemon => <DexGeneralPokemonCard handler={this.filter} key={pokemon.name} {...pokemon} />))}
            </div>
        );
    }
}

class DexGeneralPokemonCard extends React.Component {
    constructor (props) {
        super(props)

        this.state = {
            general: true
        }
    }

    toggleSpecificRender = () => {
        this.setState({general: !this.state.general})
    }

    render() {
        return(
            this.state.general ? (
                <div className="card" style={{"width": 400+ "px"}} onClick={() => {
                    this.toggleSpecificRender();
                    this.props.handler(this.props.name)
                }}>
                <img src={baseURL + "/v1/pokedex/image/" + this.props.imageName} alt={this.props.name + " image"} />
                <h3>{this.props.name}</h3>
                <h4>{"Dex no. " + this.props.dexNumber}</h4>
                <br/>
            </div>
            ):(
                <div onClick={this.toggleSpecificRender}>
                    <DexSpecificPokemonCard key ={this.props.name} {...this.props}/>            
                </div>
            )
        );
    }
}

export default Dex;
