import { baseURL } from '../global';
import React from 'react';
import PokemonCard from "./TeamPokemonCard";

class Team extends React.Component {
    state = {
        pokemons: []
    };

    async componentDidMount() {
        const teamPath = "/v1/team";
        const response = await fetch(baseURL + teamPath);
        const team = await response.json();

        this.setState({
            pokemons: team
        })
    }

    getTeam() {
        return this.state.pokemons.map((pokemon => <PokemonCard key={pokemon._id} {...pokemon} />));
    }

    render() {
        return(
            <div style={{"display": "flex", "flex-wrap": "wrap"}}>
                {this.getTeam()}
            </div>
        );
    }
}

export default Team;
