import { baseURL } from '../global';
import React from 'react';

class Inventory extends React.Component {

    state = {
        balls: null
    }

    async componentDidMount() {
        const teamPath = "/v1/inventory";
        const response = await fetch(baseURL + teamPath);
        const inventory = await response.json();
        this.setState({
            balls: inventory.balls
        })
    }


    render() {
        return(
            <div className="card">
                <Item/>
            </div>
        );
    }
}

class Item extends React.Component {
    constructor (props) {
        super(props);
    }

    render() {
        return(
            <div className="card">
                <img src={baseURL + "/v1/items/image/" + this.props.itemCode} alt={this.props.itemName + " image"} />
                <h4>{this.props.itemName}</h4>
                <p>{"Remaining" + this.props.remaining}</p>
            </div>
        );
    }
}

export default Inventory;
